INSERT INTO power_up.games VALUES (17828, '20XX', '20XX is a co-op-friendly action platformer roguelite. Explore procedurally generated levels, collect new weapons and abilities, purchase upgrades, and crush ferocious bosses! If you are a fan of roguelikes or Mega Man, 20XX is for you!', 0, '2014-11-24');
