INSERT INTO power_up.games VALUES (8628, 'Age of Zombies', 'Lock and load through the history of time as a tough-as-nails commando named Barry Steakfries, who is looking for nothing more than to shoot some zombies and make it back in time for a hearty dinner.
INSERT INTO power_up.games VALUES (1920, 'Agent', 'Agent takes players on a paranoid journey into the world of counter-intelligence, espionage and political assassinations during the height of the Cold War at the end of the 1970s.', 0, null);
