INSERT INTO power_up.games VALUES (16183, 'Air Forte', 'Air Forte is a high-altitude game of math, vocabulary, and geography. Compete with friends or fly solo in the various arenas. Good luck, pilot!', 0, '2010-08-02');
INSERT INTO power_up.games VALUES (8791, 'Air Fortress', 'Air Fortress  is a video game published in Japan for the Nintendo Entertainment System released in Japan in 1987 and later in North America in 1989. Only a total of 385 copies of the game were shipped to the United States in 1987, and it is believed that only 20 were shipped beforehand as a test run for the system. Within PAL-A regions, it was only released in Australia. This makes the American version of the game very rare.', 0, '1987-12-30');
INSERT INTO power_up.games VALUES (11443, 'Air Guardians', 'Air Guardians is a flight combat game set in the distant future. A world government is attempting to seal their grip on power, and an air force of advanced fighter craft is called upon to fight them. Fight through grand spaceship battles, night time base raids, orbital shipyard assaults, and more!', 0, null);
