INSERT INTO power_up.games VALUES (2690, 'Alien Hominid', 'They''ve got your spaceship and you will blow up anything in your path to get it back. Completely hand-drawn high-definition graphics, coupled with old-school game play, results in ultimate mayhem and hours of fun!', 0, '2004-05-20');
