INSERT INTO power_up.games VALUES (19043, 'Amigdala', 'Gherman,a middle-aged man who has abandoned all to find what lies behind the "Philadelphia experiment".11 years after the experiment,Gherman found a document that marked the coordinates of the project''s military establishment.Certainly he didn''t expect to find so much...', 0, null);
