INSERT INTO power_up.games VALUES (16365, 'Angry Birds Space', 'Play over 300 interstellar levels across 10 planets! Whether it’s the icy orbs of Cold Cuts, the volcanic explosiveness of Red Planet, Utopia – a planet made entirely of junk food, or the new Brass Hogs episode ruled by evil Hektor Porko, each episode offers intergalactic fun at every turn! And the low-gravity environment of space results in spectacular gameplay ranging from slow-motion puzzles to lightspeed destruction. With brand new birds, special abilities, and a whole galaxy to explore, the sky is no longer the limit!', 0, '2013-02-03');
