INSERT INTO power_up.games VALUES (8638, 'Ascension: Chronicle of the Godslayer', null, 0, '2011-06-29');
INSERT INTO power_up.games VALUES (17783, 'Ascension: Deckbuilding Game', 'Ascension: Chronicle of the Godslayer, was the first officially licensed deck building card game for iOS, and is now available for your PCas Ascension: Deckbuilding Game, complete with 5 full expansion decks! Play alone or with friends to battle against the Fallen One for honor and victory.', 0, '2014-11-01');
INSERT INTO power_up.games VALUES (18794, 'Ascent: The Space Game', 'Ascent - The Space Game is a space MMO set in a future where civilisation has almost completely collapsed. Players must work together to prevent total disaster, and rebuild a civilisation of their own choosing.', 0, '2016-04-10');
INSERT INTO power_up.games VALUES (19167, 'Asemblance', 'You wake to find yourself trapped inside an experimental machine. A machine built to simulate memories. You have no idea how you got here ... But to break the cycle, you must look into a past that you may not want to remember.
