INSERT INTO power_up.games VALUES (16265, 'Astro Tripper', 'Astro Tripper is a furious shoot-em-up experience inspired by the painful, knuckle busting video games of years gone by. An evolution of PomPom''s 2001 award winning game, Space Tripper. Travel the surface of large horizontally-scrolling platforms, and pit your highly maneuverable craft against hoards of enemies eager to blow you to bits, eat you or worse.', 0, '2011-08-03');
