INSERT INTO power_up.games VALUES (11645, 'Attrition Nuclear Domination', 'Attrition: Nuclear Domination is a 3d missile command style game versus up to nine other AI opponents. The game features resource collection, nuclear, and nuclear suppression devices. The player can choose to be any of the ten available nations.', 0, '2015-08-06');
INSERT INTO power_up.games VALUES (10006, 'ATV Fever', 'Fever is a fast and furious racing action game in over fifty events, and we challenge you to play it and not love it! Enjoy the great mix of environments, terrains, diverse vehicles and formats.
