INSERT INTO power_up.games VALUES (18827, 'Axes and Acres', 'Axes and Acres is a deep and engaging single-player strategy game designed to create exciting decisions every turn. The rules are easy to learn, but every turn can be played out in many different ways in order to farm, build and survive.', 0, '2016-04-06');
INSERT INTO power_up.games VALUES (14497, 'AXET', 'In this game, you control a ball. This ball moves continually up and down. You only can influence the horizontal direction. The levels are concipated with different blocks. There are different kinds of blocks and different colors. You have to destroy all "empty" blocks. You can do this by touching them with the ball. But there is a small problem: The ball must have the same color as the block you want to destroy.', 0, null);
