INSERT INTO power_up.games VALUES (18116, 'Barricade', 'Barricade is an overhead view maze arcade game released by RamTeK in 1976. Players move their blocks across the screen to create walls to try to surround their opponents and force them to crash into the walls or any block.', 0, '1976-12-30');
