INSERT INTO power_up.games VALUES (12402, 'Battle Bugs', 'In 1994, Sierra On-Line, Inc. publishes Battle Bugs, a real-time game for the DOS system. Offering strategy genre, it is now an abandonware.', 0, '1994-12-30');
