INSERT INTO power_up.games VALUES (354, 'Battlefield Play4Free', 'Battlefield Play4Free is a first-person shooter video game developed by EA Digital Illusions CE and Easy Studios and published by EA. Based on the Battlefield series, the game features a modern warfare battlefield setting.', 0, '2011-04-03');
