INSERT INTO power_up.games VALUES (23193, 'Battlerite', 'Battlerite is a Team Arena Brawler focused on adrenaline-fueled player vs player combat. Short and intense matches, stripped of random elements to optimize the action. Welcome to a world where champions dedicate their lives to the arena.', 0, '2016-09-19');
