INSERT INTO power_up.games VALUES (7790, 'Blaster Master Jr.', 'Blaster Master Jr., known as Blaster Master Boy in North America and Bomber King: Scenario 2 (ボンバーキング　シナリオ2?) in Japan, is an action video game developed by Aicom and published by Sunsoft. The game was released in 1991 for Game Boy. The game is a sequel to Robowarrior, a spin-off title in the Bomberman series by Hudson Soft. However, the game was marketed in western territories as a game in the Blaster Master series by Sunsoft.', 0, '1991-08-22');
INSERT INTO power_up.games VALUES (9387, 'Blaster Simulator', 'The player assumes the role of a blaster, who works his way up from small jobs to big ones. A level is considered to be solved, if the predetermined demolition rate has been reached and all set targets have been met.
