INSERT INTO power_up.games VALUES (20455, 'Bullet Train', 'Bullet Train is a shooter designed for use virtual reality technology to immerse players in intense combat scenarios. Players have amazing abilities such as teleportation (zip around the map and confuse enemies with sneak attacks,) slow-motion (slow down the action and make precise moves before time catches up to you,) and bullet-catching (grab projectiles out of the air and throw them back at enemies) that give them the edge in battle.', 0, null);
