INSERT INTO power_up.games VALUES (18656, 'Bunker Punks', 'Bunker Punks is a roguelike first-person shooter set in a dystopian future. You control a group of revolutionaries, raiding corporate strongholds for supplies, weapons and armor.', 0, '2016-03-29');
