INSERT INTO power_up.games VALUES (17713, 'Calvino Noir', 'The film noir stealth game. Calvino Noir is the exploratory, sneaking adventure through the 1930s European criminal underworld. Architecture meets Noir as you become unwillingly entangled in a revolutionist plot. Sneak through sewers, across rooftops, and past guards in this dark and mysterious journey.', 0, '2015-07-03');
