INSERT INTO power_up.games VALUES (18821, 'Capcom Arcade Cabinet', 'Classics Arcade Cabinet is a compilation of Capcom arcade games that was released for the PlayStation 3 and Xbox 360 from February 19, 2013 to April 17, 2013.[1] An arcade machine was made to promote the Capcom Arcade Cabinet series at the Gadget Show Live 2013 in Birmingham, UK.[2]', 0, null);
