INSERT INTO power_up.games VALUES (17407, 'Castle: Never Judge a Book by its Cover', 'Castle: Never Judge a Book by its Cover is an investigative adventure game that will immerse casual players into the thrilling exploits of famous mystery novelist Richard Castle and Detective Kate Beckett. Over the span of five chapters the player will be tasked with finding evidence, tracking down suspects, and uncovering the truth behind the copycat killer responsible for staging murder scenes depicted in his novels.', 0, '2014-04-30');
