INSERT INTO power_up.games VALUES (10571, 'Chainz 2: Relinked', 'It’s link-matching madness with Chainz 2: Relinked. Rotate the colored links to make matches of three or more in Classic Mode. Pit your link-matching skills against the clock in Arcade Mode. Ponder every precious move in Strategy Mode. Twist your brain and clear all the links in Puzzle Mode. Add in awesome sound effects, a zippy new music score, and sensationally updated visuals, and Chainz 2: Relinked is sure to keep you chained to your seat!', 0, null);
