INSERT INTO power_up.games VALUES (7597, 'Chip', 'Chip is an isometric puzzle game with robots, electricity and fancy dress! The player must help recharge Chip - a plucky little red robot - who has been imprisoned by the nefarious villain, Rust.
INSERT INTO power_up.games VALUES (6490, 'Chip ''n Dale Rescue Rangers', 'Chip ''n Dale Rescue Rangers (チップとデールの大作戦 Chippu to Dēru no Daisakusen?, lit. "Chip ''n Dale''s Mission") is a platformer video game developed and published by Capcom based on the Disney animated series of the same name. Originally released for the Nintendo Entertainment System in Japan and North America in 1990, it came to Europe the next year, and was ported to the Nintendo PlayChoice-10 arcade system. It sold approximately 1.2 million copies worldwide.', 0, '1990-06-07');
INSERT INTO power_up.games VALUES (6518, 'Chip ''n Dale Rescue Rangers 2', null, 0, '1993-12-09');
