INSERT INTO power_up.games VALUES (10086, 'Chronicles of a Dark Lord: Episode 1 Tides of Fate - Nightmare Vision', 'Delve into the mind of Magus Drakhen Lee as he becomes trapped within his own fragmented dreams.', 0, null);
