INSERT INTO power_up.games VALUES (10228, 'Chuck Norris: Bring on the Pain', null, 0, '2009-12-30');
INSERT INTO power_up.games VALUES (4516, 'Chuck Rock', null, 0, '1991-12-30');
INSERT INTO power_up.games VALUES (4371, 'Chuck Rock II: Son of Chuck', null, 0, '1993-12-30');
