INSERT INTO power_up.games VALUES (22217, 'Close Your', 'Close Your is a first person interactive short story where you live an entire life, from birth to death, and make decisions big and small that shape the outcome of that life. The catch is that each scene only lasts as long as you can keep your eyes open. The game uses your webcam to track your eyes in real life, so every time you blink, you skip forward: could be five seconds or five years. Life literally flashes before your eyes as you try desperately to hold onto each fleeting moment.', 0, null);
INSERT INTO power_up.games VALUES (8146, 'Closure', 'In the dark and mysterious world of Closure, only what you see exists. Manipulate lights to phase objects in and out of reality in this multiple-award-winning puzzle game. Play as a strange spider-like demon who explores the stories of three human characters through beautiful, eerie environments such as a decrepit factory, a murky forest, an abandoned carnival, and the strange, surreal realm that connects them together.', 0, '2012-09-06');
INSERT INTO power_up.games VALUES (10096, 'Cloud Chamber', 'Players collaborate with each other to explore, investigate and discuss the fragments of information they collect on their journey through the dataworld, writing the story themselves of a young scientist, Kathleen Petersen, who risks her sanity and betrays her father to save humanity from itself.', 0, null);
