INSERT INTO power_up.games VALUES (16820, 'Cloudbuilt', 'A game of speed, precision and freedom, use all the abilities of your rocket-powered suit to avoid fatal hazards, dodge hostile robots and reach not just the finish line, but the top of the worldwide leaderboards. Carve your own path through a multitude of mysterious floating ruins high above the clouds and show everyone you''re the best!', 0, '2014-02-03');
INSERT INTO power_up.games VALUES (19066, 'Cloudlands: VR Minigolf', 'Cloudlands is a VR minigolf experience transporting you to a beautifully landscaped course high above the clouds. Single player, online multiplayer, local multiplayer, and a course editor (coming soon) allow you to build, share, and play other user generated holes. No swing or power meters found here, simply swing the club to play.', 0, null);
