INSERT INTO power_up.games VALUES (4203, 'Comanche 4', 'The best-selling Comanche series returns with this fast-paced action game that places you at the controls of a Comanche RAH-66 helicopter. In 6 single player campaigns and multiplayer, the game combines serious firepower with effortless controls and stunning, interactive terrain.', 0, '2001-12-30');
