INSERT INTO power_up.games VALUES (23571, 'Computer Ambush', null, 0, '1982-12-30');
INSERT INTO power_up.games VALUES (11245, 'Computer Space', 'Computer Space is a video arcade game released in 1971 by Nutting Associates. Created by Nolan Bushnell and Ted Dabney, who would both later found Atari, Inc., it is generally accepted that it was the world''s first commercially sold coin-operated video game of any kind, predating the Magnavox Odyssey''s release by six months, and Atari''s Pong by one year. It was first location tested at The Dutch Goose in August 1971, then debuted at the MOA show on October 15, 1971, and then officially released in November 1971. Though not commercially sold, the coin operated minicomputer-driven Galaxy Game appeared around the same time, located solely at Stanford University.', 0, '1971-10-31');
INSERT INTO power_up.games VALUES (21926, 'Computer Stocks & Bonds', null, 0, '1981-12-31');
INSERT INTO power_up.games VALUES (2458, 'Computer Underground', 'Computer Underground (also known as C.U.) is a game published in 1993 by Haxoft, the theme is cyberpunk / dark sci-fi, puzzle-solving. It features a role-playing (rpg), simulation genre and uses a perspective.', 0, '1993-12-30');
