INSERT INTO power_up.games VALUES (3839, 'Contrast', 'CONTRAST is a puzzle/platform game where you can move between a fantastic 3D world and a mysterious shadowy universe in 2D in the blink of an eye. Delve into a dreamlike and surreal 1920s world, inspired by the performance art world of vaudeville and film noir, and cradled by a smooth and sultry jazz ambiance.', 0, '2013-11-14');
INSERT INTO power_up.games VALUES (18765, 'Controller', null, 0, '1982-12-30');
INSERT INTO power_up.games VALUES (9921, 'Convoy', 'Convoy is a tactical roguelike-like inspired by Mad Max and FTL in which you cross a wasteland in search of parts for your broken ship. 
