INSERT INTO power_up.games VALUES (11344, 'Coregrounds', 'Coregrounds is PVP tower defense done right: 2 players fight on 1 map using a unique arsenal of towers, minions and abilities. Improve your skills in this fast-paced and blissfully chaotic combat game and become the champion of the Coregrounds!', 0, null);
INSERT INTO power_up.games VALUES (19528, 'Corkscrew Follies', null, 0, '1999-11-14');
