INSERT INTO power_up.games VALUES (14400, 'Cosmic DJ', 'Cosmic DJ is a quick, "pick-up and play" musical experience about the joy of music creation for budding intergalactic musicians of all skill levels. Set among the stars, you will strive to become the legendary Cosmic DJ through six musical adventures as you’re tasked with repairing the all-important “Jamtennas” and bring harmony back to the galaxy.', 0, '2014-10-29');
