INSERT INTO power_up.games VALUES (4137, 'Covert Ops : Nuclear Dawn', 'Covert Ops: Nuclear Dawn, otherwise known as Chase the Express (チェイス・ザ・エクスプレス Cheisu Za Ekusupuresu), is a video game created by Sugar and Rockets for the PlayStation, and released in 2000. On March 16, 2000 Activision announced that they had acquired the North American publishing rights to Sony Computer Entertainment''s Japanese action-adventure game, Chase The Express. The game was then renamed Covert Ops: Nuclear Dawn for the North American market.', 0, '2000-01-26');
