INSERT INTO power_up.games VALUES (19026, 'Crashed Lander', 'Outwit gravity, race the clock, explore new worlds. Crashed lander is a gravity-based arcade game inspired by classics like Lunar Lander and Space Taxi. You''ll pilot your craft from pad to pad trying to beat your best time – then flirt with that fine line between speed and control.', 0, null);
INSERT INTO power_up.games VALUES (16327, 'Crasher', 'Crasher is a Multiplayer Online Battle Arena (MOBA) game that uses vehicles instead of human characters.', 0, '2011-01-27');
INSERT INTO power_up.games VALUES (15389, 'Crashlands', 'Crashlands is an outlandish story-driven-action-adventure set in a world of hidden magic and high technology. Tame and milk alien beasts, craft resources into menacing weaponry, befriend the locals, and go toe-to-chin with a maniac. All in the name of ON-TIME DELIVERY !
INSERT INTO power_up.games VALUES (19928, 'Crashmo', null, 0, null);
INSERT INTO power_up.games VALUES (11049, 'Crawl', 'Crawl is the local multiplayer dungeon crawler where your friends control the monsters! It’s bullet-hell beat-em-up meets old-school RPG, in an arcadey party-game for up to 4 players. One player is the hero, exploring a randomly generated dungeon full of traps and monsters - all controlled by the other players. If you slay the hero you take their place and it’s your turn to crawl! Then it''s a race to gain enough XP and loot to reach the final battle, where it''s all on one as the other players collectively control the hulking final boss!', 0, null);
