INSERT INTO power_up.games VALUES (14946, 'Crayon Physics Deluxe', 'Crayon Physics Deluxe is a 2D physics puzzle / sandbox game, in which you get to experience what it would be like if your drawings would be magically transformed into real physical objects. Solve puzzles with your artistic vision and creative use of physics.', 0, '2008-12-28');
