INSERT INTO power_up.games VALUES (10114, 'Cricket Coach 2007', 'If you''ve ever dreamed of hitting a pace bowler for six in the final over, or guiding your team to the County Championship title, then Cricket Coach 2007 is for you! Manage every aspect of your club or national side. Your decisions alone will make the difference between success and failure. With over 3700 players and 100 teams to choose from, Cricket Coach 2007 has it all.', 0, null);
