INSERT INTO power_up.games VALUES (11746, 'Criminal Girls: Invite Only', 'Criminal Girls: Invite Only a Japanese role-playing game and the enhanced version of the original Criminal Girls game. The game follows a group of girls in Hell who have to climb a tower to be reincarnated. The battle system is unique since you give the characters, each representing a different vice, orders in battle and sometimes they won’t listen to you because they haven’t been trained.', 0, '2013-11-27');
INSERT INTO power_up.games VALUES (10115, 'Criminal Minds', 'Join with the BAU to solve two thrilling cases! Hunt for clues in 25 tricky and beautiful Hidden Object Scenes. Get closer to the truth as you work with JJ, Hotch, Morgan, Prentiss, Reid, Garcia and Rossi as you interrogate suspects with special Criminal Minds Profiling Mini-games! Use the information you gather from over 40 taunting puzzles and codes to find the criminals as they try and lead you astray!', 0, null);
