INSERT INTO power_up.games VALUES (2477, 'Cruise for a Corpse', 'Cruise for a Corpse features the exclusive Cinematique operating system, designed by Delphine to offer a user friendly point and click playing style. Cinematique has been further refined since the release of Operation Stealth to offer even more depth of gameplay and a wider range of options.
INSERT INTO power_up.games VALUES (14020, 'Cruising on Broadway', null, 0, null);
