INSERT INTO power_up.games VALUES (20202, 'Dark Age of Camelot: Catacombs', null, 0, null);
INSERT INTO power_up.games VALUES (21415, 'Dark Age of Camelot: Labyrinth of the Minotaur', null, 0, null);
