INSERT INTO power_up.games VALUES (17064, 'Diehard Dungeon', 'Combining its own unique take on the real-time roguelike genre, with a nostalgic 16-bit look and feel, Diehard Dungeon is a game that feels both familiar and quite different at the same time.
INSERT INTO power_up.games VALUES (19341, 'Diep.io', 'Diep.io is a multiplayer shooting browser game like Agar.io, Wings.io and Slither.io. While playing this game, you have to control a tank to destroy the other players and obstacles.', 0, '2016-04-30');
INSERT INTO power_up.games VALUES (10185, 'DieselStormers', 'DieselStormers is a roaring run ''n'' gun for up to 4 friends in co-op multiplayer mode with RPG elements, procedural level generation and a combinatoric weapons system that lets you make your own guns from a huge number of looted parts.
INSERT INTO power_up.games VALUES (2636, 'Dig Dug', null, 0, '1982-04-18');
