INSERT INTO power_up.games VALUES (10188, 'Dogfight 1942', 'Piloting a wide range of aircraft, players engage in aerial dogfights and dangerous fighter missions across the major theaters of war during WWII. Featuring realistic aircraft, DOGFIGHT 1942 introduces cutting-edge flight dynamics with unprecedented arcade accessibility. Fly over 40 historically accurate planes, ranging from the fabled P-38 Lightning and Britain''s Spitfire, to the feared Messerschmitt 109 and Japanese Kate.', 0, null);
