INSERT INTO power_up.games VALUES (22431, 'Doom: Unto the Evil', 'Unto the Evil™ is the first of three DLC Packs bringing all-new multiplayer content to DOOM®. Players get three new maps to dominate, one new playable demon to unleash, a new weapon to master, a new equipment item to vary their arsenal, and a host of new armor sets, taunts, and Hack Modules to customize their experience.', 0, '2016-08-03');
