INSERT INTO power_up.games VALUES (17947, 'Draft Day Sports Pro Basketball 4', 'If you''ve ever wanted to step into the front office of your favorite pro hoops team this is your chance! Draft Day Sports: Pro Basketball 4 puts you in charge of your favorite professional basketball franchise. Draft, trade, sign players in free agency, and build your team into the next championship dynasty.', 0, '2014-09-30');
INSERT INTO power_up.games VALUES (18104, 'Drag Race', 'The player''s Joystick Controller is both the clutch and gear shift for his Dragster, the red button is his gas pedal. The Activision-title Dragster is an unauthorized adaptation of the 1977 Kee Games coin-op, Drag Race.', 0, '1977-12-30');
