INSERT INTO power_up.games VALUES (10247, 'Drip Drip', 'Control a team of containers and tools as you travel across the USA and visit 24 exciting cities on your mission to save the buildings from extreme damage, flooding, and destruction. Intense storms have hit each city causing water to drip everywhere. Catch the water to make cash and increase your chances of success.', 0, null);
