INSERT INTO power_up.games VALUES (13231, 'Drive!Drive!Drive!', 'Drive!Drive!Drive! is a driving game about racing multiple tracks at the same time.', 0, '2016-12-30');
INSERT INTO power_up.games VALUES (1940, 'DriveClub', 'DRIVECLUB brings to life the heart and soul of car culture. An incredible, authentic and immersive PS4 driving experience, DRIVECLUB makes you feel the exhilaration of driving the most powerful and beautifully designed cars in the world; all rendered in staggering detail, inside and out as you race them in richly detailed real-world locations alongside your Club.', 0, '2014-10-06');
