INSERT INTO power_up.games VALUES (19977, 'Dub Dash', 'Dub Dash is a fast-paced rhythm based action game including music from Bossfight and the DJs of Geometry Dash. Spin your crazy wheel in harmony with the song and keep dodging left and right avoiding obstacles at high speed. Do you rock the funky roads of Dub Dash?', 0, '2016-02-15');
