INSERT INTO power_up.games VALUES (10266, 'Dungeons: Into the Dark', 'The first DLC pack for the critically acclaimed evildoer game Dungeons delivers new challenges for wannabe dungeon lords. Heart of Into the Dark is the new campaign map Flowerdale, in which you have to deal with with ever new waves of heroes. The pack also includes ten new maps for the game modes survival, soul survival, sandbox and amusement and 20 new prestige items.', 0, null);
