INSERT INTO power_up.games VALUES (14604, 'Endless Legend: Shadows', 'This major addition to the game content includes the long-awaited espionage system (spying, infiltration and pillaging). It also includes an entire Major Faction, the Forgotten, whose gameplay is based on espionage.', 0, null);
