INSERT INTO power_up.games VALUES (14716, 'Etrian Odyssey', null, 0, '2007-01-17');
INSERT INTO power_up.games VALUES (14709, 'Etrian Odyssey 2 Untold: The Fafnir Knight', 'The Fafnir Knight is the second game in the Untold series which are to be remakes of the original Etrian Odyssey games. The games are first person RPG dungeon crawlers where you create a map of the labyrinth as you explore and complete quests.
INSERT INTO power_up.games VALUES (14717, 'Etrian Odyssey II: Heroes of Lagaard', null, 0, '2008-02-20');
