INSERT INTO power_up.games VALUES (8968, 'Everlasting Summer', 'Semyon, a poster child contemporary chan culture consumer, falls asleep in the bus, only to wake up in front of the gates of Sovyonok, a pioneer camp of the late Soviet Russia. Everlasting Summer (Бесконечное лето in Russian) is a time-warping, mind-bending visual novel with some eroge content, produced by the collective efforts of Russian chan communities.', 0, '2014-11-18');
INSERT INTO power_up.games VALUES (282, 'EverQuest', 'EverQuest is a 3D fantasy-themed massively multiplayer online role-playing game (MMORPG) released on March 16, 1999.
INSERT INTO power_up.games VALUES (23529, 'Everquest 2', null, 0, null);
INSERT INTO power_up.games VALUES (18293, 'EverQuest II', 'EverQuest® II is the epitome of massively multiplayer gaming - the ultimate blend of deep features, heritage, and community. 
