INSERT INTO power_up.games VALUES (4208, 'EverQuest II: Sentinel''s Fate', 'The sixth expansion of the groundbreaking franchise, EverQuest®II Sentinel''s Fate™ is an all-in-one compilation pack which transports players into a universe of legend and lore where adventures are just the beginning. The fabled lands of Odus, filled with epic quests, magnificent dungeons and limitless content provide the ultimate MMO experience for new players and seasoned veterans alike.', 0, '2010-02-15');
